  justify-content: flex-end;

  .header__nav-lr{
    margin-right: 40px;
    color: #C8D5F6;
    text-decoration: none;
    font-size: 16px;
    font-family: 'Montserrat';
    line-height: 27px;
    background-color: #FFF;
    width: 232px;
    height: 36px;
    border-radius: 7px;
    display: flex;
    justify-content: center;
    align-items: center;
  }

  .header__nav-lr:hover{
    color: #FFF;
    background-color: #DDE5F9;
    transition: 0.5s;
  }
`;

const ButtonEx = styled.button`
