const ProgressBarWrapper = styled.div`
  width: 100%;
  height: 20px;
  background-color: #f0f0f0;
  border-radius: 10px;
  overflow: hidden;
  margin-top: 10px;
`;

const ProgressBar = styled.div`
  height: 100%;
  width: ${(props) => props.progress}%;
  background-color: #4caf50;
  transition: width 0.4s ease;
`;

const StudLaboratory = () => {
  const [labItems, setLabItems] = useState({ isLoading: true, data: [] });
  const [searchValue, setSearchValue] = useState("");
  const [uploadProgress, setUploadProgress] = useState(0); // Прогресс загрузки
  const history = useLocation();

  const handleSearchChange = (event) => {
    setSearchValue(event.target.value);
  };

  const handleDeleteClick = (index) => {
    const updatedLabItems = [...labItems.data];
    updatedLabItems.splice(index, 1);
    setLabItems((prev) => ({ ...prev, data: updatedLabItems }));
  };

  useEffect(() => {
    const timeout = setTimeout(() => {
      setLabItems((prev) => ({ ...prev, isLoading: false }));
    }, 5000);
    return () => {
      clearTimeout(timeout);
    };
  }, []);

  // Пример загрузки файла с использованием axios и прогресс-бара
  const handleFileUpload = (file) => {
    const formData = new FormData();
    formData.append('file', file);

    axios.post('http://0.0.0.0:8002/upload', formData, {
      onUploadProgress: (progressEvent) => {
        const progress = Math.round((progressEvent.loaded * 100) / progressEvent.total);
        setUploadProgress(progress);
      },
      headers: {
        'Content-Type': 'multipart/form-data'
      }
    })
    .then((response) => {
      console.log('File uploaded successfully:', response.data);
    })
    .catch((error) => {
      console.error('Error uploading file:', error);
    });
  };

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      handleFileUpload(file);
    }
  };

  useEffect(() => {
    fetch('http://0.0.0.0:8002/tasks', {
      method: 'GET',
      headers: {
        'accept': 'application/json'
      }
    })
    .then((response) => {
      if (response.ok) {
        return response.json();
      } else {
        throw new Error("Failed to fetch tasks");
      }
    })
    .then((data) => {
      const tasksArray = Object.keys(data).map((key) => ({
        id: key,
        description: data[key],
      }));
      setLabItems({ isLoading: false, data: tasksArray });
    })
    .catch(error => {
      console.error(error.message);
    });
  }, []);

  const getColors = (index) => {
    return index % 2 === 0 ? "section__lab-list-ready" : "section__lab-list";
  };

  return (
    <PageWrapper>
      <ContentWrapper>
        <SectionLab>
          <div className="section__lab-blockSearch">
            <input
              className="section__lab-input"
              value={searchValue}
              onChange={handleSearchChange}
              placeholder="Введите имя лабораторной"
            />
            <button className="section__lab-button">Поиск</button>
          </div>

          <input type="file" onChange={handleFileChange} />

          {/* Отображение прогресс-бара */}
          {uploadProgress > 0 && (
            <ProgressBarWrapper>
              <ProgressBar progress={uploadProgress} />
            </ProgressBarWrapper>
          )}

          <ListLab>
            {labItems.isLoading ? (
              <p>Загрузка лабораторных заданий...</p>
            ) : (
              labItems.data
                .filter((item) =>
                  item.description.toLowerCase().includes(searchValue.toLowerCase())
                )
                .map((item, index) => (
                  <li key={item.id} className={getColors(index)}>
                    <NameLab>
                      {item.description}
                      <button onClick={() => handleDeleteClick(index)}>Удалить</button>
                    </NameLab>
                  </li>
                ))
            )}
          </ListLab>
        </SectionLab>
      </ContentWrapper>
      <Footer />
    </PageWrapper>
  );
};

export default StudLaboratory;
