import "./style.css";
import logo from "../../img/logo.svg";
import React , {useRole} from "react";
import Bread from "../BreadCrumbs";
import { Link } from "react-router-dom";
import styled from 'styled-components';
import { LABORATORY_ROUTE, PROFILE_ROUTE, REGISTRATION_ROUTE , STUDLABORATORY_ROUTE } from '../../app/routing/route';

const HeaderStyle = styled.header`
  width: 100%;
  height: 100%;
  background-color: #C8D5F6; 
`;

const HeaderWrapper = styled.div`
  display: flex;
  justify-content: space-between;
  padding: 12px 0px 0px 60px;
`;

const Nav = styled.nav`
  padding: 20px 0px 30px 0px;
  display: flex;
  justify-content: flex-end;

  .header__nav-lr{
    margin-right: 40px;
    color: #C8D5F6;
    text-decoration: none;
    font-size: 16px;
    font-family: 'Montserrat';
    line-height: 27px;
    background-color: #FFF;
    width: 232px;
    height: 36px;
    border-radius: 7px;
    display: flex;
    justify-content: center;
    align-items: center;
  }

  .header__nav-lr:hover{
    color: #FFF;
    background-color: #DDE5F9;
    transition: 0.5s;
  }
`;

const ButtonEx = styled.button`
  margin-right: 40px;
  color: #C8D5F6;
  text-decoration: none;
  font-size: 16px;
  font-family: 'Montserrat';
  line-height: 27px;
  background-color: #FFF;
  width: 232px;
  height: 36px;
  border-radius: 7px;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const Header = ({ setIsLoggedIn, isLoggedIn, isButtonClicked }) => {

    const handleLogout = () => {
      setIsLoggedIn(false);
    };
    return ( 
        <>
            <HeaderStyle>
                <HeaderWrapper>
                    <div>
                        <Link to="/">
                            <img src={logo} alt="логотип"/>
                        </Link>    
                    </div>
                    {/* {isButtonClicked && ( */}
                    <Nav>
                      {isLoggedIn && (
                        <>
                          <Link to={PROFILE_ROUTE}>
                            Личный кабинет
                          </Link>
                          {userRole === 'teacher' && (
                            <Link to={LABORATORY_ROUTE}>
                              Лабораторные работы
                            </Link>
                          )}
                          {userRole === 'stud' && (
                            <Link to={STUDLABORATORY_ROUTE}>
                              Лабораторные работы
                            </Link>
                          )}
                          <ButtonEx onClick={handleLogout}>
                            Выйти
                          </ButtonEx>
                        </>
                      )}
                      {!isLoggedIn && (
                        <>
                          <HeaderLink to={REGISTRATION_ROUTE}>
                            Регистрация
                          </HeaderLink>
                        </>
                      )}
                    </Nav>
                    {/* // )} */}
                </HeaderWrapper>
            </HeaderStyle>
            <Bread/>
        </>
     );
}

export default Header;
