import React, { useState } from 'react';
import styled from 'styled-components';

const Form = styled.form`
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 45px;
    border: 2px dashed ${({ isDragging }) => (isDragging ? '#808080' : '#FFFFFF')};
    background-color: ${({ isDragging }) => (isDragging ? '#F5F5F5' : '#FFFFFF')};
    transition: border-color 0.3s ease, background-color 0.3s ease;
`
const Button = styled.button`
    width: 1098px;
    height: 75px;
    background-color: #FFFFFF;
    color: #000000;
    cursor: pointer;
    border-radius: 10px;
    border-style: none;
    font-family: 'Montserrat';
    font-size: 16px;
`
const Inpt = styled.input`
    margin: 0;
    padding: 0;
    width: 0;
    height: 0;
    visibility: hidden;
`
const Label = styled.label`
    width: 1098px;
    height: 75px;
    background-color: #FFFFFF;
    color: #000000;
    cursor: pointer;
    border-radius: 10px;
    border-style: none;
    font-family: 'Montserrat';
    font-size: 16px;
`

const FileUploader = () => {
    const [files, setFiles] = useState([]);
    const [isDragging, setIsDragging] = useState(false);

    const handleChange = (e) => {
        e.preventDefault()
        if(e.target.files && e.target.files[0]){
            setFiles([...e.target.files])
        }
    }

    const handleDragOver = (e) => {
        e.preventDefault();
        setIsDragging(true);
    }

    const handleDragLeave = () => {
        setIsDragging(false);
    }

    return (
        <Form
            isDragging={isDragging}
            onDragOver={handleDragOver}
            onDragLeave={handleDragLeave}
            onDrop={handleDragLeave} // Reset dragging state on drop
        >
            <Label htmlFor="fileInput">
                <span>
                    Загрузите файл
                </span>
                <Inpt id="fileInput" type="file" multiple={true} onChange={handleChange}/>
            </Label>
            {files.length > 0 && <ul>
                {files.map(({name}, id)=> <li key={id}>{name}</li>)}    
            </ul>}
            <Button type="submit">
                Отправить на проверку
            </Button>
        </Form>
    );
};

export default FileUploader;
