import React, { useState, useRef } from 'react';
import styled from 'styled-components';

const StyledFileInput = styled.input`
  /* display: none; */
`;

const StyledLabel = styled.label`
  padding: 10px 20px;
  background-color: #4CAF50;
  color: white;
  border: none;
  border-radius: 5px;
  cursor: pointer;
`;

const FileUploader = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [fileUrl, setFileUrl] = useState(null);
  const fileInputRef = useRef(null);

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setSelectedFile(file);
    setFileUrl(URL.createObjectURL(file));
  };

  return (
    <div>
      <StyledFileInput type="file" onChange={handleFileChange} ref={fileInputRef} />
      <StyledLabel htmlFor="fileInput">Выбрать файл</StyledLabel>
      {fileUrl && (
        <div>
          {selectedFile.type.includes('image') ? (
            <img src={fileUrl} alt="uploaded file" style={{ maxWidth: '100px' }} />
          ) : (
            <a href={fileUrl} download>Скачать файл</a>
          )}
        </div>
      )}
    </div>
  );
};

export default FileUploader;
