import React, { useRef } from 'react';
import styled from 'styled-components';

const StyledFileInput = styled.input`
  display: none;
`;

const Label = styled.label`
  padding: 10px 20px;
  background-color: #4CAF50;
  color: white;
  border: none;
  border-radius: 5px;
  cursor: pointer;

  &:hover {
    background-color: #45a049;
  }
`;

const FileUploader = () => {
  const fileInputRef = useRef();

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    console.log('Выбранный файл:', file);
  };

  const handleDragOver = (e) => {
    e.preventDefault();
    e.stopPropagation();
    // Добавьте стили или классы, которые должны применяться при перетаскивании файла
    e.target.style.border = '2px dashed #45a049';
  };

  const handleDragLeave = (e) => {
    e.preventDefault();
    e.stopPropagation();
    // Удалите стили или классы, которые были применены при перетаскивании файла
    e.target.style.border = 'none';
  };

  return (
    <div>
      <Label htmlFor="fileInput">Загрузите файл</Label>
      <StyledFileInput
        type="file"
        id="fileInput"
        ref={fileInputRef}
        onChange={handleFileChange}
        onDragOver={handleDragOver}
        onDragLeave={handleDragLeave}
      />
    </div>
  );
};

export default FileUploader;
