import React, { useState } from 'react';
import styled from 'styled-components';

const Form = styled.form`
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 45px;
`;

const Button = styled.button`
    width: 1098px;
    height: 75px;
    background-color: #FFFFFF;
    color: #000000;
    cursor: ${({ disabled }) => (disabled ? 'not-allowed' : 'pointer')};
    border-radius: 10px;
    border-style: none;
    font-family: 'Montserrat';
    font-size: 16px;
    margin-bottom: 20px;
    opacity: ${({ disabled }) => (disabled ? 0.5 : 1)};

    &:hover {
        background-color: ${({ disabled }) => (disabled ? '#FFFFFF' : '#E2EDD0')};
        color: #000;
        transition: 0.3s;
    }
`;

const Inpt = styled.input`
    display: none;
`;

const Label = styled.label`
    border: 2px dashed #000;
    height: 250px;
    text-align: center;
    border-radius: 7px;
    margin-top: 10px;
    width: 1098px;
    transition: background-color 0.3s;
    background-color: #FFFFFF;

    &:hover {
        background-color: #E2EDD08C;
    }
`;

const Span = styled.span`
    display: flex;
    justify-content: center;
    align-items: center;
    width: 100%;
    height: 100%;
    background-color: ${({ isDragging }) => (isDragging ? '#F5F5F5' : 'transparent')};
    transition: background-color 0.3s ease;
`;

const ProgressBarWrapper = styled.div`
    width: 100%;
    height: 20px;
    background-color: #f0f0f0;
    border-radius: 10px;
    overflow: hidden;
    margin-top: 10px;
`;

const ProgressBar = styled.div`
    height: 100%;
    width: ${({ progress }) => progress}%;
    background-color: #4caf50;
    transition: width 0.4s ease;
`;

const ProgressText = styled.div`
    margin-top: 5px;
    font-size: 14px;
    color: #000;
`;

const FileUploader = ({ onSubmit }) => {
    const [file, setFile] = useState(null);
    const [isDragging, setIsDragging] = useState(false);
    const [isHovered, setIsHovered] = useState(false);
    const [uploadProgress, setUploadProgress] = useState(0);
    const [isUploading, setIsUploading] = useState(false);

    const handleChange = (e) => {
        if (e.target.files && e.target.files[0]) {
            setFile(e.target.files[0]);
            setUploadProgress(0); // Сбросить прогресс при выборе нового файла
        }
    };

    const handleDragEnter = (e) => {
        e.preventDefault();
        setIsDragging(true);
    };

    const handleDragLeave = () => {
        setIsDragging(false);
    };

    const handleDrop = (e) => {
        e.preventDefault();
        setIsDragging(false);

        if (e.dataTransfer.files && e.dataTransfer.files[0]) {
            setFile(e.dataTransfer.files[0]);
            setUploadProgress(0); // Сбросить прогресс
        }
    };

    const handleFileUpload = () => {
        if (!file) return;

        setIsUploading(true); // Начинаем загрузку

        // Симуляция загрузки файла с постепенным обновлением прогресса
        const fakeUpload = setInterval(() => {
            setUploadProgress((prevProgress) => {
                const newProgress = prevProgress + 10;

                if (newProgress >= 100) {
                    clearInterval(fakeUpload);
                    setIsUploading(false); // Завершить загрузку
                    return 100;
                }

                return newProgress; // Увеличение прогресса на 10% за шаг
            });
        }, 500); // Шаг обновления каждые 0.5 сек
    };

    return (
        <Form
            onDragEnter={handleDragEnter}
            onDragOver={handleDragEnter}
            onDragLeave={handleDragLeave}
            onDrop={handleDrop}
        >
            <Label
                htmlFor="fileInput"
                isHovered={isHovered}
                onMouseEnter={() => setIsHovered(true)}
                onMouseLeave={() => setIsHovered(false)}
            >
                <Span isDragging={isDragging}>
                    {file ? file.name : 'Загрузите файл'}
                </Span>
                <Inpt
                    id="fileInput"
                    type="file"
                    onChange={handleChange}
                    onMouseEnter={() => setIsHovered(true)}
                    onMouseLeave={() => setIsHovered(false)}
                />
            </Label>

            {/* Прогресс-бар, отображаемый под зоной перетаскивания файла */}
            {file && (
                <ProgressBarWrapper>
                    <ProgressBar progress={uploadProgress} />
                </ProgressBarWrapper>
            )}

            {/* Отображение текста прогресса */}
            {file && <ProgressText>{isUploading ? `${uploadProgress}%` : ''}</ProgressText>}

            <Button 
                type="button" 
                onClick={handleFileUpload} 
                disabled={!file || isUploading}
            >
                {isUploading ? 'Загрузка...' : 'Отправить на проверку'}
            </Button>
        </Form>
    );
};

export default FileUploader;
