import React, { useRef } from 'react';
import styled from 'styled-components';

const Button = styled.button`
    width: 1098px;
    height: 75px;
    background-color: #FFFFFF;
    color: #000000;
    cursor: pointer;
    border-radius: 10px;
    border-style: none;
    font-family: 'Montserrat';
    font-size: 16px;
`;

const FileUploadButton = () => {
  const fileInputRef = useRef(null);

  const handleButtonClick = () => {
    fileInputRef.current.click();
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    // Здесь вы можете обработать выбранный файл или отправить его на сервер
    console.log('Выбранный файл:', file);

    // Пример отправки файла на сервер с использованием Fetch API
    const formData = new FormData();
    formData.append('file', file);

    fetch('URL_ВАШЕГО_СЕРВЕРА', {
      method: 'POST',
      body: formData,
    })
    .then(response => {
      // Обработка ответа от сервера
    })
    .catch(error => {
      // Обработка ошибки
      console.error('Ошибка при отправке файла:', error);
    });
  };

  return (
    <>
      <Button onClick={handleButtonClick}>Выбрать файл</Button>
      <input
        type="file"
        ref={fileInputRef}
        style={{ position: 'absolute', left: '-9999px' }}
        onChange={handleFileChange}
      />
    </>
  );
};

export default FileUploadButton;
