import "./style.css"
import logo from "../../img/logo.svg"
import React, { useState } from "react";
import Bread from "../BreadCrumbs";
import { Link } from "react-router-dom";
import styled from 'styled-components'

const HeaderStyle = styled.header`
    width: 100%;
    height: 100%;
    background-color: #C8D5F6; 
`
const Nav = styled.nav`
    padding: 20px 0px 30px 0px;
    display: flex;
    justify-content: flex-end;
`
const ButtonEx = styled.button`
    margin-right: 40px;
    color: #C8D5F6;
    text-decoration: none;
    font-size: 16px;
    font-family: 'Montserrat';
    line-height: 27px;
    background-color: #FFF;
    width: 232px;
    height: 36px;
    border-radius: 7px;
    display: flex;
    justify-content: center;
    align-items: center;
`
const Header = ({ setIsLoggedIn, isLoggedIn, isButtonClicked }) => {


    const handleLogout = () => {
        setIsLoggedIn(false)
    }

    return ( 
        <>
            <HeaderStyle>
                <div className="header__wrapper">
                    <div>
                        <img src={logo} className="header__img-logo" alt="логотип"></img>    
                    </div>
                    {/* {isButtonClicked && ( */}
                        <Nav>
                            <Link to="/laboratory"className="header__nav-lr">
                                Лабараторная работа
                            </Link>
                            <Link to="/profile" className="header__nav-lr">
                                Личный кабинет
                            </Link>
                            {isLoggedIn  ? (
                                <ButtonEx onClick={handleLogout}>
                                    Выйти
                                </ButtonEx>
                            ) : (
                                <Link to="/Registration" className="header__nav-lr">
                                    Регистрация
                                </Link>
                            )}
                        </Nav>
                    {/* // )} */}
                </div>

            </HeaderStyle>
            <Bread/>
        </>
     );
}

export default Header;
