    background-color: #FFFFFF;
    border: none;
    outline: none;
    font-size: 16px;
`
const NameLabInput = styled.textarea`
    display: flex;
    border: none;
    background: none;
    width: 608px;
    height: 260px;
    font-size: 18px;    
    padding: 15px 0px 0px 35px;;
    outline: none;
`

const LaboratoryAdd = () => {
  const [labTitle, setLabTitle] = useState("");
  const [labDescription, setLabDescription] = useState("");
  const [tests, setTests] = useState([]);
  const [newTest, setNewTest] = useState({
    nameInput: "",
    input: "",
    format: "",
    output: "",
  });
  const [maxVariable, setMaxVariable] = useState("");
  const [maxTime, setMaxTime] = useState("");
  const [formula, setFormula] = useState("");
  const [responseMessage, setResponseMessage] = useState("");

  const handleLabTitleChange = (event) => {
    setLabTitle(event.target.value);
  };

  const handleLabDescriptionChange = (event) => {
    setLabDescription(event.target.value);
  };

  const handleNewTestChange = (field, value) => {
    setNewTest((prevState) => ({
      ...prevState,
      [field]: value,
    }));
  };

  const handleAddTest = () => {
    setTests([...tests, newTest]);
    setNewTest({
      nameInput: "",
      input: "",
      format: "",
      output: "",
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    const newData = {
      lab_task: {
        task_text: labTitle,
        task_description: labDescription,
        name: tests.map((test) => test.nameInput),
        input: tests.map((test) => test.input),
        output: tests.map((test) => test.output),
        formula: formula,
      },
      max_variable: maxVariable,
      max_time: maxTime,
    };

    sendDataToServer(newData);
  };

  const sendDataToServer = async (data) => {
    try {
      const response = await axios.post("http://0.0.0.0:8002/newtask", data);
      console.log("Ответ от сервера:", response.data);
      setResponseMessage("Лабораторная работа успешно добавлена!");
    } catch (error) {
      console.error("Ошибка при отправке запроса:", error);
      setResponseMessage("Ошибка при добавлении лабораторной работы");
    }
  };

  return (
    <>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="labTitle">Название лабораторной работы:</label>
          <input
            type="text"
            id="labTitle"
            value={labTitle}
            onChange={handleLabTitleChange}
          />
        </div>
        <div>
          <label htmlFor="labDescription">Описание лабораторной работы:</label>
          <textarea
            id="labDescription"
            value={labDescription}
            onChange={handleLabDescriptionChange}
          ></textarea>
        </div>
        <div>
          <h3>Список тестов:</h3>
          {tests.map((test, index) => (
            <div key={index}>
              <h4>Тест {index + 1}: "{test.nameInput}"</h4>
              <div>
                <label>Входные данные:</label>
                <input type="text" value={test.input} readOnly />
              </div>
              <div>
                <label>Формат ввода:</label>
                <input type="text" value={test.format} readOnly />
              </div>
              <div>
                <label>Вывод:</label>
                <input type="text" value={test.output} readOnly />
              </div>
            </div>
          ))}
        </div>
        <div>
          <h3>Добавить новый тест:</h3>
          <div>
            <label htmlFor="newTestName">Название теста:</label>
            <input
              type="text"
              id="newTestName"
              value={newTest.nameInput}
              onChange={(event) => handleNewTestChange("nameInput", event.target.value)}
            />
          </div>
          <div>
            <label htmlFor="newTestInput">Входные данные:</label>
            <input
              type="text"
              id="newTestInput"
              value={newTest.input}
              onChange={(event) => handleNewTestChange("input", event.target.value)}
            />
          </div>
          <div>
            <label htmlFor="newTestFormat">Формат ввода:</label>
            <input
              type="text"
              id="newTestFormat"
              value={newTest.format}
              onChange={(event) => handleNewTestChange("format", event.target.value)}
            />
          </div>
          <div>
            <label htmlFor="newTestOutput">Вывод:</label>
            <input
              type="text"
              id="newTestOutput"
              value={newTest.output}
              onChange={(event) => handleNewTestChange("output", event.target.value)}
            />
          </div>
          <button type="button" onClick={handleAddTest}>
            Добавить тест
          </button>
        </div>
        <div>
          <label htmlFor="maxVariable">Количество переменных:</label>
          <input
            type="text"
            id="maxVariable"
            value={maxVariable}
            onChange={(event) => setMaxVariable(event.target.value)}
          />
          <button type="button" onClick={() => localStorage.setItem('maxVariable', maxVariable)}>
            Сохранить
          </button>
          <button type="button" onClick={() => setMaxVariable('')}>
            Удалить
          </button>
        </div>
        <div>
          <label htmlFor="maxTime">Скорость работы:</label>
          <input
            type="text"
            id="maxTime"
            value={maxTime}
            onChange={(event) => setMaxTime(event.target.value)}
          />
          <button type="button" onClick={() => localStorage.setItem('maxTime', maxTime)}>
            Сохранить
          </button>
          <button type="button" onClick={() => setMaxTime('')}>
            Удалить
          </button>
        </div>
        <div>
          <label htmlFor="formula">Формула:</label>
          <input
            type="text"
            id="formula"
            value={formula}
            onChange={(event) => setFormula(event.target.value)}
          />
          <button type="button">Добавить формулу</button>
        </div>
        <button type="submit" onClick={() => {
          sendDataToServer(newData);
          alert('Вы успешно добавили новую лабораторную работу');
        }}>
          <Link to='/Laboratory'>
            Завершить редактирование и добавить лабораторную
          </Link>
        </button>
        <p>{responseMessage}</p>
      </form>
    </>
  );
};

export default LaboratoryAdd;
