`;

const FileUploader = () => {
    const fileComponent = useRef()
    const handlerSubmit = (event) => {
        event.preventDefault()
        console.log(fileComponent.current.files[0])
    }   
  return (
    <form action="" onSubmit={handlerSubmit}>
        <label htmlFor="">
            Загрузите файл
        </label>
        <input 
            type="file" 
            ref={fileComponent}
        />

    </form>
  );
};

export default FileUploader;
